<?phprequire_once 'app/comun/config.php';include_once 'app/comun/session.php';require_once 'app/comun/BD.php';$CAT  = new Catalogo();$prepareJSON = array();//$_POST['q'] = 'modelos';//$_POST['id_marca'] = 1;switch ($_POST['q']) {    /**********************************************************************************/    case 'marcas' :        $result = $CAT->getMarcas (                            array(                                'CARROCERIA_ID_CARROCERIA' => $_POST['id_carroceria']                            )                        );        while($row = $result->fetch_assoc()) {            $item = array (                               "id"   => mb_strtoupper($row['MARCA_ID_MARCA']),                               "text" => mb_strtoupper($row['MARCA_NOMBRE'])                           );            array_push($prepareJSON, $item);          }        break;    /**********************************************************************************/    case 'modelos' :        $result = $CAT->getModelos (                            array(                                'MODELO_ID_MARCA'      => $_POST['id_marca'],                                'MODELO_ID_CARROCERIA' => $_POST['id_carroceria']                            )                        );        while($row = $result->fetch_assoc()) {            $item = array (                               "id"   => mb_strtoupper($row['MODELO_ID_MODELO']),                               "text" => mb_strtoupper($row['MODELO_NOMBRE'])                           );            array_push($prepareJSON, $item);          }        break;    /**********************************************************************************/    case 'ciudades' :        $result = $CAT->getCiudades (                            array(                                'CIUDAD_ID_REGION' => $_POST['id_region']                            )                        );        while($row = $result->fetch_assoc()) {            $item = array (                               "id"   => mb_strtoupper($row['CIUDAD_ID_CIUDAD']),                               "text" => mb_strtoupper($row['CIUDAD_NOMBRE'])                           );            array_push($prepareJSON, $item);          }        break;    /**********************************************************************************/    case 'automotoras' :        $result = $CAT->getAutomotoras (                            array(                                'AUTOMOTORA_ID_AUTOMOTORA' => $_SESSION['AUTOMOTORA_ID_AUTOMOTORA'],                                //'AUTOMOTORA_SUCURSAL'      => $_SESSION['AUTOMOTORA_ID_AUTOMOTORA']                                        )                        );        while($row = $result->fetch_assoc()) {            $item = array (                               "id"   => mb_strtoupper($row['AUTOMOTORA_ID_AUTOMOTORA']),                               "text" => mb_strtoupper($row['AUTOMOTORA_NOMBRE'])                           );            array_push($prepareJSON, $item);          }        break;    /**********************************************************************************/}$JSON = json_encode($prepareJSON);echo $JSON;?>